import React, { Component } from "react";
import Header from "../components/Header";
import Container from "../components/Container";
// import API from "../utils/API";
import users from "../user.json";
import "../components/Container/style.css"

class Home extends Component {

state = {
    user: users,
    Email: "",
    Passowrd:""
}

handleInputChange = event => {
    event.preventDefault();
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });

    console.log(this.state.Email);
    console.log(this.state.Passowrd);
  };


render() {
    return(
        <div>
            <Header  />
            <div class="wrapper fadeInDown">
            <div id="formContent">
            {/* <!-- Tabs Titles --> */}

            {/* <!-- Icon --> */}
                <div class="fadeIn first">
                <img src="https://img.icons8.com/material/24/000000/gender-neutral-user.png" id="icon" alt="User Icon" />
                </div>

                {/* <!-- Login Form --> */}
                <form>
                <input type="text" id="login" class="fadeIn second" name="Email" placeholder="Email Address" onChange={this.handleInputChange}/>
                <input type="text" id="password" class="fadeIn third" name="Passowrd" placeholder="Password" onChange={this.handleUpdateValueChange}/>
                <input type="submit" class="fadeIn fourth" value="Log In"/>
                </form>

                {/* <!-- Remind Passowrd --> */}
                <div id="formFooter">
                <a class="underlineHover" href="#">Forgot Password?</a>
                </div>

            </div>
            </div>
        </div>
    );
}


}

export default Home;
